CREATE VIEW `nodes_online` AS select "owner_node_num", "node_num", "last_heard", "snr", "is_favorite", "is_ignored", "user_id", "long_name", "short_name", "macaddr", "hw_model", "role", "public_key", "is_licensed", "latitude_i", "longitude_i", "altitude", "position_time", "position_precision_bits", "ground_speed", "ground_track", "sats_in_view", "battery_level", "voltage", "channel_utilization", "air_util_tx", "uptime_seconds", "private_note", "updated_at" from "nodes" where last_heard IS NOT NULL AND last_heard > (unixepoch() - 900);